import Step from "@mui/material/Step";
import StepLabel from "@mui/material/StepLabel";
import Stepper from "@mui/material/Stepper";
import { useState } from "react";
import { ILoanStepper } from "../../types";
import { CustomButton } from "../custom-button";
import { FlexCol, FlexRow } from "../display";
import { H6 } from "../typography";

export const LoanStepper = ({
  steps,
  active = 0,
  onNext,
  onBack,
  onReset,
  onSubmit,
  alternativeLabel = false,
}: ILoanStepper) => {
  const [activeStep, setActiveStep] = useState(active);

  const handleNext = () => {
    if (onNext) {
      onNext();
    }

    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    if (onBack) {
      onBack();
    }

    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleReset = () => {
    if (onReset) {
      onReset();
    }
    setActiveStep(0);
  };

  return (
    <FlexCol gap={1} width={"100%"}>
      <Stepper activeStep={activeStep} alternativeLabel={alternativeLabel}>
        {steps.map((step) => (
          <Step key={step.name}>
            <StepLabel>
              <H6>{step.label}</H6>
            </StepLabel>
          </Step>
        ))}
      </Stepper>
      {steps[activeStep].component}
      <FlexRow justifyContent={"space-between"}>
        <FlexRow gap={1}>
          <CustomButton
            variant="text"
            sx={{ color: "grey" }}
            onClick={handleReset}
          >
            Reset
          </CustomButton>
          {activeStep !== 0 && (
            <CustomButton
              variant="text"
              sx={{ color: "grey" }}
              onClick={handleBack}
            >
              Back
            </CustomButton>
          )}
        </FlexRow>
        {activeStep === steps.length - 1 ? (
          <CustomButton color="primary" onClick={onSubmit}>
            Submit
          </CustomButton>
        ) : (
          <CustomButton color="primary" onClick={handleNext}>
            Next
          </CustomButton>
        )}
      </FlexRow>
    </FlexCol>
  );
};
