import { useTheme } from "@mui/material";
import { useState } from "react";
import {
  BottomButtons,
  FlexCol,
  H4,
  ICustomButton,
  LoanDetailsForm,
  LoanStepper,
  PersonalDetailsForm,
} from "../../components";
import { LoanFormData, PersonalDetailsFormValues } from "../../types";

const defaultValue: LoanFormData = {
  firstName: "",
  lastName: "",
  email: "",
  employmentStatus: "employed",
  employerName: "",
  loanPurpose: "vehicle",
  amount: null,
  deposit: null,
  loanTerm: null,
};

export const LoanApplication = () => {
  const [activeStep, setActiveStep] = useState(0);
  const [formData, setFormData] = useState<LoanFormData>(defaultValue);

  const handleNext = () => {
    setActiveStep(activeStep + 1);
  };

  const handleBack = () => {
    setActiveStep(activeStep - 1);
  };

  const personalDetailsNext = (data: PersonalDetailsFormValues) => {
    setFormData((prev) => ({
      ...prev,
      ...data,
    }));

    // do something if needed
    handleNext();
  };

  const resetFormData = () => {
    setFormData(defaultValue);
  };

  const backButton: ICustomButton = {
    option: "enable",
    variant: "text",
    sx: { color: "gray" },
    onClick: () => {
      setActiveStep(activeStep - 1);
    },
    text: "Back",
  };

  const againButton: ICustomButton = {
    option: "enable",
    variant: "contained",
    color: "warning",
    text: "Again",
    onClick: () => {
      setActiveStep(0);
      resetFormData();
    },
  };

  const theme = useTheme();
  const steps = [
    {
      name: "personal-details",
      label: "Personal Details",
      component: (
        <PersonalDetailsForm
          defaultValue={defaultValue}
          onNext={personalDetailsNext}
          onReset={() => {
            setFormData((prev) => ({
              ...prev,
              loanPurpose: "vehicle",
              amount: null,
              deposit: null,
              loanTerm: null,
            }));
          }}
          formData={formData}
        />
      ),
    },
    {
      name: "loan-details",
      label: "Loan Details",
      component: (
        <LoanDetailsForm
          defaultValue={defaultValue}
          onNext={handleNext}
          onBack={handleBack}
          onReset={() => {
            setFormData((prev) => ({
              ...prev,
              firstName: "",
              lastName: "",
              email: "",
              employmentStatus: "employed",
              employerName: "",
            }));
          }}
          formData={formData}
        />
      ),
    },
    {
      name: "suggestions",
      label: "Loan Suggestions",
      component: (
        <BottomButtons
          buttonProps={{
            primary: againButton,
            destructive: backButton,
          }}
        />
      ),
    },
  ];

  return (
    <FlexCol gap={2} padding={theme.spacing(4, 4)}>
      <H4 alignSelf={"center"}>Apply For Loan</H4>
      <LoanStepper steps={steps} activeStep={activeStep} alternativeLabel />
    </FlexCol>
  );
};
