import { useTheme } from "@mui/material";
import { useState } from "react";
import { FlexCol, H4, ICustomButton, LoanStepper } from "../../components";

export const LoanApplication = () => {
  const [activeStep, setActiveStep] = useState(0);

  const nextButton = (text: string = "Next"): ICustomButton => ({
    option: "enable",
    variant: "contained",
    text,
    onClick: () => {
      setActiveStep(activeStep + 1);
    },
  });

  const resetButton = (
    onReset: () => void,
    text: string = "Reset"
  ): ICustomButton => ({
    option: "enable",
    variant: "text",
    sx: { color: "gray" },
    onClick: onReset,
    text,
  });

  const backButton = (text: string = "Back"): ICustomButton => ({
    variant: "text",
    sx: { color: "gray" },
    onClick: () => {
      setActiveStep(activeStep - 1);
    },
    option: "enable",
    text,
  });

  const theme = useTheme();
  const steps = [
    {
      name: "personal-details",
      label: "Personal Details",
      component: <></>,
      bottomButtons: {
        primary: nextButton("Next"),
        secondary: resetButton(() => {}),
      },
    },
    {
      name: "loan-details",
      label: "Loan Details",
      component: <></>,
      bottomButtons: {
        primary: nextButton("Submit"),
        secondary: resetButton(() => {}),
        destructive: backButton(),
      },
    },
    {
      name: "suggestions",
      label: "Loan Suggestions",
      component: <></>,
      bottomButtons: {
        primary: { option: "hide" } as ICustomButton,
        destructive: backButton(),
      },
    },
  ];

  return (
    <FlexCol gap={2} padding={theme.spacing(4, 4)}>
      <H4 alignSelf={"center"}>Apply For Loan</H4>
      <LoanStepper steps={steps} activeStep={activeStep} alternativeLabel />
    </FlexCol>
  );
};
