import { useTheme } from "@mui/material";
import { useState } from "react";
import { FlexCol, H4, ICustomButton, LoanStepper } from "../../components";

export const LoanApplication = () => {
  const [activeStep, setActiveStep] = useState(0);

  const nextButton: ICustomButton = {
    option: "enable",
    variant: "contained",
    text: "Next",
    onClick: () => {
      setActiveStep(activeStep + 1);
    },
  };

  const getCommonButton = (
    handleClick: () => void,
    text: string = "Reset"
  ): ICustomButton => ({
    option: "enable",
    variant: "text",
    sx: { color: "gray" },
    onClick: handleClick,
    text,
  });

  const againButton: ICustomButton = {
    option: "enable",
    variant: "contained",
    color: "warning",
    text: "Again",
    onClick: () => {
      setActiveStep(0);
    },
  };

  const submitButton: ICustomButton = {
    option: "enable",
    variant: "contained",
    text: "Submit",
    onClick: () => {},
  };

  const theme = useTheme();
  const steps = [
    {
      name: "personal-details",
      label: "Personal Details",
      component: <></>,
      bottomButtons: {
        primary: nextButton,
        secondary: getCommonButton(() => {}),
      },
    },
    {
      name: "loan-details",
      label: "Loan Details",
      component: <></>,
      bottomButtons: {
        primary: submitButton,
        secondary: getCommonButton(() => {}),
        destructive: getCommonButton(() => {
          setActiveStep(activeStep - 1);
        }, "Back"),
      },
    },
    {
      name: "suggestions",
      label: "Loan Suggestions",
      component: <></>,
      bottomButtons: {
        primary: againButton,
        destructive: getCommonButton(() => {
          setActiveStep(activeStep - 1);
        }, "Back"),
      },
    },
  ];

  return (
    <FlexCol gap={2} padding={theme.spacing(4, 4)}>
      <H4 alignSelf={"center"}>Apply For Loan</H4>
      <LoanStepper steps={steps} activeStep={activeStep} alternativeLabel />
    </FlexCol>
  );
};
